$(".revres a").click(function(){
    
  var post_link = $(this).attr("href");; 
  var meta = $(this).attr("rel");
  var st = $(this).data("st");
  var that = $(this); 
    
     $.ajaxSetup({
            type: "POST",
            cache:false,
            url: post_link,
            data: {
                "status": st,
                "postid": +meta,
            },
         
            complete: function () {
                console.log( 'Удача ' );
                that.addClass(st);
            }
         });

    $(this).html(svgloader);
    that.load(post_link); 
    window.setTimeout(function(){location.reload()},1000) 
      return false;
    });	  
    

$('form.revsend').submit(function() {

    $('form.revsend input[type="submit"]').fadeOut('normal', 
       function() {
            $(this).parent().append(svgloader);
		});
            
    var ajaxpag = $(this).attr('action');
    
    var idarticle = $(this).find('.idarticle').val();
    var idReviewer = $(this).find('.idReviewer').val();
    var autmail = $(this).find('.autmail').val();
    var letter = $(this).find('.letter').val();

    $.ajaxSetup({
            type: "POST",
            cache:false,
            url: ajaxpag,
        data: {
             "idarticle": idarticle, 
             "idReviewer": idReviewer, 
             "autmail": autmail, 
             "letter": letter, 
               },
         
            complete: function () {
                console.log( 'Удача ' );
            }
         });
   $(this).load(ajaxpag); 
   window.setTimeout(function(){location.reload()},1000) 
		
		return false;
	});
    
 

$(".post-link").click(function(){
   
    $("#container").remove();
    $("#sitback").remove();

var posid = $(this).attr("rel");

    $.ajaxSetup({ 
        cache:false,
        type: "POST", 
        data: {"getpost" : +posid }, 
    });
    
$("html").css("overflow-y","hidden");
    
var post_link = $(this).attr("data-link");
	$("body").append('<div id="container"></div>');
	$("body").append('<div id="sitback"></div>');

    
	$("#container").html(svgloader);
	$("#container").load(post_link);
     
$("#sitback").animate({opacity: "0.6"}, 300 );
      return false;
    });	


$(document).on("click", "#sitback", function(event){
    $("#container").remove();
    $("#sitback").remove();
    $("html").css("overflow-y","auto"); 
	window.history.back(); 
});    
    
    
    

