var keyupDelay = 500;
$(document).ready(function () {
    $('#textfield').keyup(function () {
        delay(applyFilter, keyupDelay);
    });
});
var delay = (function () {
    var timer = 0;
    return function (callback, ms) {
        clearTimeout(timer);
        timer = setTimeout(callback, ms);
    };
})();

function applyFilter() {
    var textfieldText = $('#textfield').val();
    $('#item-container .autline').hide().filter(function () {
        var itemValue = $(this).attr('value');
        var itemText = $(this).text();
        if (itemValue.indexOf(textfieldText) >= 0 || itemText.indexOf(textfieldText) >= 0) {
            return true;
        } else {
            return false;
        }
    }).fadeIn();
}

